mapper.select:
select
  dep_num, dep_name, dep_level, dep_budget
from department


        and dep_num = #{depNum}


        and dep_name = #{depName}


        and dep_level = #{depLevel}


        and dep_budget = #{depBudget}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from department


        and dep_num = #{depNum}


        and dep_name = #{depName}


        and dep_level = #{depLevel}


        and dep_budget = #{depBudget}
