mapper.select:
select
  work_num, name, dep_num, phone, password, state
from staff


        and work_num = #{workNum}


        and name = #{name}


        and dep_num = #{depNum}


        and phone = #{phone}


        and password = #{password}


        and state = #{state}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from staff


        and work_num = #{workNum}


        and name = #{name}


        and dep_num = #{depNum}


        and phone = #{phone}


        and password = #{password}


        and state = #{state}

mapper.update:
update staff


        state = false,


where work_num = #{work_num}

update staff


        state = true,


where work_num = #{workNum}

update staff


        in_service = false,


where work_num = #{workNum}
