mapper.select:
select
  work_order_num, work_order_type, work_order_name, apply_time, worker_num, file, work_order_state, reason
from work_order


        and work_order_num = #{workOrderNum}


        and work_order_type = #{workOrderType}


        and work_order_name = #{workOrderName}


        and apply_time = #{applyTime}


        and worker_num = #{workerNum}


        and file = #{file}


        and work_order_state = #{workOrderState}


        and reason = #{reason}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from work_order


        and work_order_num = #{workOrderNum}


        and work_order_type = #{workOrderType}


        and work_order_name = #{workOrderName}


        and apply_time = #{applyTime}


        and worker_num = #{workerNum}


        and file = #{file}


        and work_order_state = #{workOrderState}


        and reason = #{reason}
